INSERT INTO `novels` (`novel_id`, `user_id`, `title`, `author_name`, `translator_name`, `description`, `cover_image_url`, `status`, `view_count`, `created_at`, `updated_at`) VALUES
(1, 3, 'ชีวิตเกษียณของผู้ถูกย้อยเวลา', '', '', 'เรื่องย่อ หลังจากที่เกทแรกเปิดในปี 2018 ลิม แดอิน รอดชีวิตมาได้ 20 ปี ขณะที่โลกเชื่อมต่อกับมิติอื่น วันรุ่งขึ้นหลังจากการเฉลิมฉลองการเกษียณอายุที่รอคอยมานาน เขาก็ได้ย้อนเวลากลับไป 20 ปีย้อนกลับไปตอนที่ไม่มีอะไรเลย....', '/image/novel/1756952041_7f545b9a-0d08-4600-800b-f7636582f9af.png', 'ongoing', 19, '2025-09-03 21:13:37', '2025-09-08 03:34:55'),
(2, 3, 'ระบบสร้างซอมบี้', '', '', 'โจวเฉียงได้รับระบบสร้างซอมบี้ แต่มีเงื่อนไข ทุกๆ สามวันเขาต้องเอาชีวิตรอดในโลกที่เต็มไปด้วยซอมบี้ โชคดีที่มีระบบทำให้เขาเรียนรู้ที่จะอยู่กับพวกมัน หลังจากทำความคุ้นเคยกับโลกหายนะนี้ เขาพบว่าที่นี่มีเทคโนโลยีกว่าโลกปัจจุบันถึง 30 ปี แถมระบบยังมีฟังก์ชั่นให้นำซอมบี้ที่สร้างกลับไปยังโลกปัจจุบันได้อีก อะไรจะเกิดขึ้นกับอนาคตของโจวเฉียงมาติดตามไปพร้อมกัน...', '/image/novel/1756955124_5221b0ad-5d3d-4ffa-927f-ce189024c082.png', 'ongoing', 38, '2025-09-03 22:05:24', '2025-09-07 17:39:43'),
(4, 3, 'บันทึกตำนานราชันอหังการ [ 剑道第一仙 ]', '', '', 'เสียงคำรามกราดเกรี้ยวดังเลือนลั่นฟ้า การจากไปของผู้ยิ่งใหญ่นำมาซึ่งความโกลาหนทั่วเก้ามหาแดนดิน เหล่าคนละโมบต่างคิดหมายแย่งชิงสิ่งวิเศษที่ผู้เป็นยอดปรมาจารย์เคยปกครองยุคสมัยครอบครอง ทว่านั่นหาใช่จุดจบ แต่มันคือจุดเริ่มต้น! แท้จริงแล้วภายในโลงสัมฤทธิ์ มันว่างเปล่า! ไร้ศพ ไร้คน และไร้ซึ่งดาบเก้าคุมขัง!!\n\nซูอี้ หัวหน้าศิษย์สายนอกสำนักดาบชิงเหอ บุตรเขยตระกูลเหวิน ผู้สูญเสียพลังภายในตันเถียนไปทั้งหมด! แท้จริงแล้วเมื่อหนึ่งปีก่อนเขาได้ตื่นขึ้น และจดจำเรื่องราวเมื่อครั้งยังเป็น ‘ปรมาจารย์ดาบเสวียนจวิน’ อดีตผู้ปกครองเพียงหนึ่ง ณ ยุคสมัยแห่งเก้ามหาแดนดินได้!\n\n…ครานั้น เหล่าคนต้อยต่ำต่างหมายแย่งชิงลักขโมยสิ่งของของข้า พวกมันกล้าวอ้างคุณธรรม บ้างก็อ้างป้ายสีใส่ร้ายข้า ทว่าบัดนี้ข้ากลับมาแล้ว! และอีกไม่นานจะกลับมาผงาดอีกครั้ง!', '/image/novel/1757244789_62789bebSaP1P9fk-193x278.jpeg', 'ongoing', 1, '2025-09-06 11:04:06', '2025-09-10 02:51:13'),
(5, 3, 'เซียนคีย์บอร์ด [陆地键仙]', '六如和尚', '', 'รายละเอียด\n\nซูอัน ชายหนุ่มที่วันๆใช้ชีวิตคลุกตัวอยู่แต่ในโลกออนไลน์ เที่ยวยั่วยุ ปั่นประสาทผู้คนในโลกออนไลน์ให้ไม่เป็นสุขอยู่ทุกวี่วัน เป็นนักเลงคีย์บอร์ดที่ใครๆต่างก็ต้องเข็ดขยาด แต่แล้ววันหนึ่ง ไม่รู้ว่าฟ้าลงทัณฑ์หรือสวรรค์บัญชา ให้เขาคนนี้ต้องพบจุดจบชีวิตที่น่าอนาถโดนฟ้าผ่าตายในขณะที่กำลังเปิดศึกคีย์บอร์ดกับคนบนโลกออนไลน์อยู่ในห้องของตัวเอง หากแต่นี่ไม่ใช่จุดจบ!\n\nชายหนุ่มรู้สึกตัวฟื้นขึ้นมาในร่างใหม่ที่มีตำแหน่งเป็นถึงเขยใหญ่ตระกูลอ๋องฉู่ที่แสนมั่งคั่งแถมยังมีภรรยาที่สวยหยาดเยิ้มเป็นอันดับหนึ่งของมณฑลที่ใครๆต่างก็หมายปอง\n\nดูเหมือนว่าเส้นทางชีวิตใหม่ของเขาจะโรยด้วยกลีบกุหลาบสินะ?\n\nไม่เลย!\n\nบนโลกใบใหม่ที่เขาฟื้นขึ้นมานั้นเต็มไปด้วยผู้บ่มเพาะพลังเหนือมนุษย์มากมายแถมตำแหน่งลูกเขยอ๋องฉู่กลับทำให้เขาต้องคอยเผชิญจากการถูกลอบสังหารซ้ำแล้วซ้ำเล่าโดยที่เขาก็ไม่เข้าใจว่าไอ้เจ้าของร่างคนก่อนไปสร้างศัตรูเอาไว้มากมายขนาดนี้ได้ยังไง? ซ้ำร้ายร่างที่เขามาสิงสู่กลับเป็นแค่ร่างที่ไม่ได้ดีเด่ไม่มีพรสวรรค์เป็นเลิศเหมือนใครเขา ทุกคนในโลกนี้จึงต่างมองเขาเป็นแค่ไอ้คนไร้ค่าที่โชคดีถูกคุณหนูใหญ่ลูกสาวคนโตของอ๋องฉู่เลือกให้เป็นสามี อย่างเดียวที่พอจะภูมิใจได้ก็มีแต่หน้าตาที่หล่อเหลาและไอเทมสุดล้ำที่ติดตัวเขามาด้วย ซึ่งมันคือ “คีย์บอร์ดเซียน!!” ระบบสุดโกงตัวช่วยที่จะทำให้เขาใช้ชีวิตยียวนผู้คนในโลกใบใหม่แห่งนี้ได้โดยที่ไม่ตายก่อนวัยอันควร!\n\nขอเชิญติดตามเรื่องราวการผจญภัยสุดป่วนในโลกที่ไม่ค่อยจะสงบสุขเท่าไหร่อยู่แล้ว โดยเฉพาะเมื่อ ซูอัน หนุ่มสายเกรียนเซียนปั่นประสาทที่ทะลุมิติมาพร้อมระบบคีย์บอร์ดสุดยียวนคู่ใจ ความโกลาหลครั้งมโหฬารยิ่งกว่าเดิมจึงอุบัติขึ้นจนทุกคนในโลกแห่งการบ่มเพาะไม่ว่าจะเป็นจักรพรรดิผู้แข็งแกร่งที่สุดในใต้หล้าหรือแม้แต่ขอทานยังต้องอุทานเป็นเสียงเดียวกัน\n\n“ซูอัน! แกมันไอ้ตัวบัดซบ!!”\n\n**เนื้อหาบางส่วนของนิยายแปลเรื่องนี้ อาจมีเนื้อหาเกี่ยวกับเพศ และความรุนแรง**\n\n(ผู้อ่านที่มีอายุน้อยกว่า 18 ปี ควรได้รับคำแนะนำ)', '/image/novel/1757238129_6192208aBy6WoSao.png', 'ongoing', 54, '2025-09-07 04:42:09', '2025-09-07 17:41:30'),
(6, 3, 'พันธสัญญาลวงรัก', '', '', 'ถงเหมี่ยวเหมี่ยวหมั้นกับมู่อวี้เฉิงอย่างคลุมถุงชน ฉากหน้าพวกเขาเป็นคู่รัก แต่ความจริงแล้วพวกเขาเหมือนคนไม่รู้จักกัน จนวันหนึ่งมู่อวี้เฉิงเมาและทั้งสองก็มีสมพันธ์กันโดยที่มู่อวี้เฉิงไม่รู้และจำไม่ได้\n\nสามเดือนต่อมา..\n\nเธอกำลังตั้งครรภ์อยู่ โดยที่มู่อวี้เฉิงยังไม่รู้ ขณะที่เธออยู่ในบ้านคนเดียว กริ่งประตูหน้าบ้านก็ดังขึ้น..', '/image/novel/1757239337_6541d756-193x278.png', 'ongoing', 21, '2025-09-07 05:02:17', '2025-09-07 10:59:54'),
(7, 3, 'เจี้ยนกู่ เซียนกระบี่สยบหล้า', '', '', 'เจี้ยนกู่ เซียนกระบี่สยบหล้า', '/image/novel/1757239566_655b2988e.png', 'ongoing', 6, '2025-09-07 05:06:06', '2025-09-07 16:59:59'),
(8, 3, ' Ongoing เปิดระบบสุดโกงอัปสกิลหมอ', '', '', 'เปิดระบบสุดโกงอัปสกิลหมอ', '/image/novel/1757239764_61d44445LSpjhqcZ.png', 'ongoing', 1, '2025-09-07 05:09:24', '2025-09-07 10:59:37'),
(9, 3, 'คัมภีร์วิถีเซียน', 'ว่างอวี่ (忘语)', '', 'คัมภีร์วิถีเซียน (จบบริบูรณ์)', '/image/novel/1757240125_lAuMPqaB-1.png', 'ongoing', 0, '2025-09-07 10:12:36', '2025-09-07 10:59:35'),
(10, 3, 'The Second Coming of Gluttony', '', '', 'เขาเป็นผีพนัน ขี้แพ้ มนุษย์ที่น่าขยะแขยง แต่ว่าความฝันก็ได้ปลุกตัวเขาที่ไร้สติอยู่จนตื่นขึ้น\nเขาจะใช้ความสามารถพิเศษที่เขามี และใช้ความฝันเพื่อบุกเบิกเส้นทางของตนในโลกที่มีเป็นที่รู้จักกันในชื่อลอส พาราไดท์\n…\n“บุตรแห่งเทพกู่ลาได้หวนคืนมาแล้ว”\nฉันได้ติดอยู่ในโลกแห่งการพนัน ฉันได้หันหลังให้กับครอบครัวและกระทั่งทรยศต่อคนรัก\nฉันได้ใช้ชีวิตไปอย่างไร้ค่า ฉันมันเป็นเศษสวะ ความเป็นจริงได้บอกกับฉันเช่นนี้\nไม่ว่าฉันจะมีเงินแค่ไหนฉันก็สูญเสียมันไปจนหมด\nเพื่อที่จะเปลื่ยนชีวิตที่น่าสมเพชนี้ ฉันได้เลือกที่จะไปสู่จินตนาการแทน แต่ถึงแบบนั้นมันก็ยังเป็นเช่นเดิม\nฉันสงสัยว่าฉันจะรอดไปถึงปลายทางของถนนที่ยาวไหลนี้ได้ไหม\nแต่แล้วฉันก็ต้องถูกบังคับให้ต้องคุกเข่าต่อหน้าองค์กรที่ทรงพลัง\nสิ่งที่ฉันได้บากบั่นสร้างขึ้นมาด้วยมือตัวเองได้พังทลายลงอย่างไร้ค่า\nอีกแค่ครั้งเดียวเท่านั้น ฉันอยากที่จะบอกให้ตัวฉันเองรู้ถึงความจริงเรื่องนี้\nมานี่สิ บุตรข้า…\nในคราวนี้ฉันจะไม่ลีลาอีกต่อไปแล้ว', '/image/novel/1757241242_The-Second-Coming-of-Avarice.jpg', 'ongoing', 5, '2025-09-07 10:31:22', '2025-09-07 16:56:32'),
(11, 3, 'Delicious by Touch สูตรลับ ฉบับสัมผัส', '', '', '‘โกกยองฮา’  หนุ่มพนักงานพาร์ทไทม์ร้านอาหารผู้มีชีวิตแสนธรรมดา\nจู่ๆ วันหนึ่งก็พบกับเหตุการณ์ประหลาดเมื่อได้แตะถ้วยแกง…\nเขามองเห็นข้อความลอยอยู่กลางอากาศ และข้อความที่ว่า คือคำอธิบายสูตรอาหาร!\nทันทีที่ก้าวเท้าสู่เส้นทางพ่อครัว ชีวิตเขาก็ไม่ธรรมดาอีกเลย…\nYou may also like', '/image/novel/1757241351_5daed82eOuihcLOs.jpg', 'draft', 0, '2025-09-07 10:35:51', '2025-09-10 02:56:10'),
(12, 3, 'เกิดใหม่ชาตินี้… ขอเป็นเจ้านิกายมาไลฟ์สด', '', '', 'ฉู่ลั่ว เด็กสาวผู้ถูกสลับตัว! ครอบครัวที่แท้จริงคือตระกูลฉู่ที่ร่ำรวยมีชื่อเสียง เธอฝันหวานไปว่าจะได้มีครอบครัวที่สมบูรณ์แบบ แต่สิ่งที่ได้รับกลับมาคือความเย็นชาและการเลือกปฏิบัติ เพราะพวกเขาเห็นลูกสาวตัวปลอมดีกว่าเธอ! ฉู่หร่าน คุณหนูผู้เลิศเลอเพอร์เฟ็กต์ได้รับความรักไปทั้งหมด หลังจากเกือบจมน้ำตายเพราะทะเลาะกับฉู่หร่าน… ฉู่ลั่วก็เปลี่ยนไปเป็นคนละคน กลายเป็นสาวมาดนิ่งเย็นชา ใช้เวลาไปกับการไลฟ์สด แก้ปัญหาเรื่องภูติผีและวิญญาณให้เหล่าสาวกในช่องสตรีม คนทั่วไปคงคิดว่าเด็กสาวเสียใจจนบ้าไปแล้ว คาดไม่ถึงเลยว่าระหว่างจมน้ำ ฉู่ลั่วจะได้ไปบำเพ็ญยังแดนเซียน! เธอกลับมาอีกครั้งเหมือนเกิดใหม่ เพื่อฟื้นคืนพลังวิญญาณและเอาชนะลูกรักสวรรค์อย่างฉู่หร่านให้ได้ …ช่องสตรีมสดของเจ้านิกายหญิงฉู่ลั่วจึงได้เวลาขึ้นไลฟ์แล้ว!', '/image/novel/1757241472_N1lN39-193x278.jpeg', 'ongoing', 0, '2025-09-07 10:37:53', '2025-09-07 10:59:29'),
(13, 3, 'ทะลุมิติมาเป็นแม่เลี้ยง ข้าพลิกฟื้นทั้งครอบครัว', '', '', 'ฉินเหยา ทะลุมิติจากวันสิ้นโลกที่มีซอมบี้ พืชและสัตว์กลายพันธุ์มาอยู่ในยุคโบราณแสนสงบสุข\n\nเจ้าของร่างเดิมคือ เหยาเหนียง หญิงสาวอายุ 18 ปี ที่ลี้ภัยมาจนถึงหมู่บ้านตระกูลหลิว\n\nระหว่างลี้ภัยครอบครัวของนางตายหมดทำให้ต้องยอมรับการจัดสรรของทางการแต่งงานกับ หลิวจี้ พ่อหม้ายหนุ่มแห่งหมู่บ้านตระกูลหลิว\n\nแม้เขาจะมีลูกแล้วถึงสี่คนแต่เพื่อเริ่มต้นชีวิตใหม่ประกอบกับใบหน้าหล่อเหลาของหลิวจี้ นางจึงตอบรับการแต่งงานนี้ ปรากฏว่าเพียงแต่งเข้ามาหลิวจี้ก็โยนภาระหน้าที่การเลี้ยงเด็กๆ ทั้งสี่และงานบ้านงานไร่ทั้งหลายมาให้นางจนหมด ส่วนเขาก็หายตัวไปไม่กลับบ้าน ทำให้เหยาเหนียงที่ร่างกายอ่อนแอและหิวโหยจากการลี้ภัยต้องไปทำงานในไร่ สุดท้ายก็ไม่ฟื้นขึ้นมาอีก นั่นทำให้ฉินเหยาทะลุมิติมาอยู่ในร่างนี้แทน\n\nฉินเหยาพยายามใช้ชีวิตท่ามกลางสภาพแวดล้อมที่ขาดแคลนให้ดีที่สุด ด้วยความสามารถพิเศษที่ติดตัวนางมาจากโลกโน้นทำให้นางมีพละกำลังที่มากกว่าคนธรรมดาทั่วไป ทั้งยังเพียบพร้อมด้วยทักษะในการดำรงชีวิตมากมาย\n\nชีวิตของอดีตนักล่าซอมบี้จึงต้องเผชิญกับความท้าทายใหม่คือการทำไร่และเป็นหัวหน้าครอบครัว\n\nเมื่อคิดว่าไม่น่าจะเอาดีด้านการทำไร่ได้ นางก็ต้องลองหาวิธีอื่นเพื่อพลิกฐานะครอบครัวให้สุขสบายขึ้นให้จงได้!', '/image/novel/1757241661_aileenQCSjP-193x278.jpeg', 'ongoing', 4, '2025-09-07 10:41:01', '2025-09-07 10:59:28'),
(14, 3, 'เด็กหญิงผู้เป็นที่รักของเหล่าวายร้าย', '', '', 'ค่ำคืนอันเงียบสงัดคืนหนึ่ง ’เสี่ยวซิงซิง‘ เด็กหญิงกำพร้าวัย 3 ขวบ ได้รับภารกิจจากเทียนเต้าเทวดาที่มาเข้าฝันบอกว่าเธอต้องทำภารกิจหยุดยั้งหายนะที่กำลังจะเกิดขึ้น ทั้งเขายังบอกว่าตนเองเป็นเด็กมีพรสวรรค์ ฉะน้้นภารกิจนี้ต้องสำเร็จแน่นอน! แล้วภารกิจกอบกู้โลกก็ตกเป็นหน้าที่ของซิงซิงวัย 3 ขวบคนนี้ แต่สิ่งที่ไม่รู้คือความชั่วร้ายนั้นดันก่อขึ้นโดยฝีมือของบิดาและเหล่าบรรดาพี่ ๆ ของเธอน่ะสิ! เด็กหญิงตัวน้อยไม่รู้เลยครอบครัวของมีตัวความชั่วร้ายอะไรแฝงไว้อยู่ และไม่รู้ว่าต้องทำอย่างไรถึงจะทำภารกิจนี้สำเร็จ แต่เสี่ยวซิงซิงคนนี้จะใช้ความน่ารักและอ่อนโยนของตนหยุดความชั่วร้ายเหล่านั้น ซิงซิงจะปกป้องทุกคนเอง!', '/image/novel/1757241756_c84a700-193x278.jpeg', 'ongoing', 0, '2025-09-07 10:42:36', '2025-09-07 10:59:25'),
(15, 3, 'เกิดใหม่ทั้งทีขอลิขิตรักเอง', '', '', 'ในชาติที่แล้ว เธอถูกพ่อแม่ผู้ให้กำเนิดของตัวเองโกหกหลอกลวงมานับครั้งไม่ถ้วน\n\nพวกเขาได้รับเงินที่หามาได้อย่างยากลำบากของพ่อแม่บุญธรรมของเธอ แต่พวกเขากลับบอกว่าตัวเองนั้นเป็นคนส่งเสียเธอเรียนด้วยตนเอง\n\nพวกเขาเผาจดหมายตอบรับจากทางมหาวิทยาลัยของเธอ เพียงเพราะอยากจะให้เธอแต่งงานออกเรือนไป และเพียงเพื่อพวกเขาจะได้เงินสินสอดทองหมั้นอันล้ำค่าเหล่านั้น\n\nพวกเขาหลอกล่อให้เธอนำเงินของสามีตัวเองมาซื้อบ้านให้พวกเขา แต่หลังจากที่พวกเขาย้ายเข้าไป พวกเขาก็ไม่ได้เอ่ยถึงเงินจำนวนนั้นอีกเลย\n\nในตอนที่เธอถูกแม่สามีขับไล่ให้ออกไปจากบ้าน ครอบครัวทางฝ่ายสามีก็เอาแต่คิดว่าทำไมพวกเขาถึงได้ลูกสะใภ้ที่ไม่มีทั้งเงิน ไม่มีทั้งบ้านแบบนี้ด้วย\n\nหลังจากที่เธอได้ชีวิตกลับมาอีกครั้ง เธอจะต้องหลีกหนีจากพ่อแม่ผู้ให้กำเนิดที่เห็นแก่เงิน และน้องชายที่คิดอยากจะให้พี่สาวของตัวเองแต่งงานออกเรือนไปเพื่อเงิน\n\nและสิ่งที่สำคัญที่สุดก็คือ เธอจะต้องตอบแทนบุญคุณของพ่อแม่บุญธรรมที่ทั้งรักและห่วงใยเธอ และค้นหาสามีที่แสนดีซึ่งยอมทำลายอนาคตของตัวเองเพื่อเธอให้จงได้\n\nในชาตินี้ เธอสาบานว่าเธอจะเป็นภรรยาที่ดี!', '/image/novel/1757241875_novelpdf01ba7bf3a-193x278.jpeg', 'ongoing', 2, '2025-09-07 10:44:35', '2025-09-07 10:59:23'),
(16, 3, 'ฉันมีพี่ชาย 7 คน', '', '', 'แค่…หนวนหน่วน น้องสาวผู้พลัดพรากยิ้ม เหล่าพี่ชายก็ใจละลาย\n\nอายุแค่ห้าขวบแต่หนวนหน่วนก็ต้องลุกมาขึ้นเขาผ่าฟืนให้อาหารไก่เสียแล้ว ชีวิตน้อย ๆ ช่างอาภัพนัก ชายชราที่คอยเลี้ยงดูก็ตายจาก ทิ้งเธอไว้ในกระท่อมโกโรโกโสเพียงลำพัง แต่แล้ววันหนึ่งรถหรูก็มาจอดเทียบถึงบ้าน บอกว่าเธอเป็นสมบัติล้ำค่าตระกูลกู้ หนวนหน่วนฟังไม่ผิดใช่ไหม ตระกูลที่รวยซะยิ่งกว่ารวยนั่นน่ะเหรอ? แล้วพี่ชายทั้งเจ็ดคนของตระกูลนี้ก็ไม่ธรรมดากันทั้งนั้น พวกเขาจะต้อนรับเธอในฐานะน้องสาวหรือเปล่านะ? เหล่านักเผือกของหมู่บ้านเองก็ทำตาโตเท่าไข่ห่านหลังเห็นคนที่ลงมาจากรถ ที่บอกว่าหนวนหน่วนเป็นลูกสาวที่หายไปหมายความว่ายังไง โอ๊ย น่าอิจฉาซะจริง! ได้ยินมาว่าลูกชายตระกูลนั้นสืบทอดใบหน้าหล่อเหลากันมาทุกคน แถมยังเพียบพร้อมไปด้วยความสามารถ บางคนเป็นถึงดาราดังเลยนะ! เหอะ เอาเถอะ หนวนหน่วนไปอยู่ที่นั่นก็คงถูกรังแก ไม่มีทางถูกรักหรอก ถึงจะคิดแบบนั้น แต่ไหงได้ข่าวอีกที เด็กคนนี้ถึงกลายเป็นดวงใจพวกพี่ชายกันได้นะ หนวนหน่วนทำได้ยังไงกัน!', '/image/novel/1757242119_Yqkg-193x278.jpeg', 'ongoing', 0, '2025-09-07 10:48:39', '2025-09-07 10:59:21'),
(17, 3, 'ทะลุมิติมาช่วยสามี (ว่าที่เศรษฐี) ในยุค 70', '', '', 'ลี่หรงทะลุมิติมาอยู่ในนิยายที่ตัวละครชื่อเหมือนเธอ จากลี่หรงคนก่อนที่คอยทำลายครอบครัวสามี มาคราวนี้แหละเธอจะช่วยให้สามีร่ำรวยเอง\n\nจากอุบัติเหตุทางรถยนต์ พลันทำให้หญิงสาวได้ทะลุมิติมาในนิยายย้อนยุคที่เธอเคยอ่าน เป็น ‘ลี่หรง’ นางเอกนิยายที่ชื่อเดียวกับเธอ แถมมีสามีสุดเย็นชา ‘จ้าวชิงซง’ คอยจับตาดูเธออีกด้วย จากหญิงสาวคนเดิมที่ไม่ลงรอยกับสามี มาคราวนี้ลี่หรงตั้งใจจะเริ่มชีวิตใหม่โดยการช่วยให้สามีของเธอร่ำรวยขึ้นให้ได้', '/image/novel/1757242234_novelpdfv3Br1q-193x278.jpeg', 'ongoing', 1, '2025-09-07 10:50:34', '2025-09-07 10:59:19'),
(31, 2, 'ยอดนักรบจอมราชัน', '', '', 'เขาคือผู้ปกครองที่อยู่เหนือเหล่าทหารรับจ้างและหน่วยรบพิเศษ เขาคือผู้น่าเกรงขามที่สามารถทำให้ผู้นำประเทศแต่ละประเทศถึงกับสั่นคลอน! เพื่อพวกพ้องของเขาแล้วเขาพร้อมที่จะเสียสละตัวเอง เพื่อครอบครัวของเขา..เขาก็ไม่ลังเลที่จะหลั่งเลือด! เขานั้นดุจดั่งมังกรที่ทยานขึ้นเหนือสรวงสวรรค์.. พลังของเขานั้นทำให้ได้มาซึ่งชัยชนะ ไม่ว่าสถานการณ์จะเสียเปรียบและย้ำแย่เพียงใดก็ไม่มีสิ่งใดสามารถหยุดเขาได้ แม้ว่าจะเป็นสายลมหรือผืนน้ำก็ตาม…', '/image/novel/1757472252_ปกยอดนักรบจอมราชัน.jpg', 'ongoing', 2, '2025-09-10 02:44:12', '2025-09-09 21:47:19'),
(32, 2, 'ระบบจอมยุทธ์สุดโกงแห่งโลกคู่ขนาน', '', '', 'ฟางผิงกลับมาเกิดใหม่ในวัย 18 ปีในโลกที่ไม่เหมือนเดิมพร้อมระบบประหลาด และที่นี่เองที่เขาได้ก้าวเข้าสู่โลกของการฝึกยุทธ์', '/image/novel/1757472554_zzz_001-724x1024.jpg', 'draft', 0, '2025-09-10 02:49:14', '2025-09-10 02:50:35');

